// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

contract BNBEnviroment is ERC1155 {
    // Constantes
    uint256 public constant BNB_TOKEN = 0;

    // Variables
    using Counters for Counters.Counter;
    Counters.Counter private _totalFNFTs;
    mapping (uint256 => string) _listUris;
    // Variables para ERC20
    string private _nameToken;
    string private _symbolToken;
    uint8 private _decimalsToken;
    uint256 private _totalSupplyTokens;

    // Eventos
    event CreateToken( address indexed  owner, uint256 indexed _id, uint256 _amount, bytes _data);
    event DeleteToken( address indexed  owner, uint256 indexed _id, uint256 _amount);

    // Modificadores

    constructor() ERC1155("") {
        // ERC20
        _nameToken = "BNB Token";
        _symbolToken = "BNB";
        _decimalsToken = 18;
        _totalSupplyTokens = 10**_decimalsToken;
        _mint(msg.sender, BNB_TOKEN, _totalSupplyTokens, "");

        // ERC721
        _listUris[BNB_TOKEN] = "https://bafkreigaodcf2yaiubhvh7jko57zeruwr6k2zcej6ftv4aglg7mtoosz7u.ipfs.nftstorage.link/";
    }

    // Funciones ERC20

    function nameToken() public view returns(string memory){
        return _nameToken;
    }

    function symbolToken() public view returns(string memory){
        return _symbolToken;
    }

    function decimalsToken() public view returns(uint8){
        return _decimalsToken;
    }

    function totalSupplyToken() public view returns(uint256){
        return _totalSupplyTokens;
    }

    function balanceOfToken() public view returns(uint256){
        return balanceOf(msg.sender, BNB_TOKEN);
    }

    function transferToken(address _to, uint256 _amount ) public {
        safeTransferFrom(msg.sender, _to, BNB_TOKEN, _amount, "");
    }

    // Funciones ERC721

    function balanceOfFNFT(address _owner) external view returns (uint256){

    }

    function uri(uint256 _tokenid) override public view returns (string memory) {
        return _listUris[_tokenid];
    }

    function createFNFT(string memory _urlURI) public returns(uint256){
        _totalFNFTs.increment();
        uint256 _id = _totalFNFTs.current();
        _listUris[_id] = _urlURI;
        _mint(msg.sender, _id, 1, "");
        emit CreateToken( msg.sender, _id, 1, "");
        return _id;
    }

    function createFNFTBatch(string[] memory _urlURI) public returns( uint256[] memory){
        uint256[] memory _ids = new uint[](_urlURI.length);
        for (uint i = 0; i < _urlURI.length; i++) {
            _totalFNFTs.increment();
            uint256 _id = _totalFNFTs.current();
            _listUris[_id] = _urlURI[i];
            _mint(msg.sender, _id, 1, "");
            emit CreateToken( msg.sender, _id, 1, "");
            _ids[i] = _id;
        }
        return _ids;
    }    
}

contracts/ICO.sol.content:
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract MyToken is IERC20, IERC20Metadata, Ownable {
    using SafeMath for uint256;

    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;

    string private _name;
    string private _symbol;
    uint8 private _decimals;
    uint256 private _totalSupply;

    uint256 private _maxSupply;
    bool private _maxSupplyEnabled;

    constructor(string memory name_, string memory symbol_, uint8 decimals_, uint256 totalSupply_, uint256 maxSupply_) {
        _name = name_;
        _symbol = symbol_;
        _decimals = decimals_;
        _totalSupply = totalSupply_.mul(10 ** decimals_);
        _maxSupply = maxSupply_.mul(10 ** decimals_);
        _balances[msg.sender] = _totalSupply;
        _maxSupplyEnabled = true;
        emit Transfer(address(0), msg.sender, _totalSupply);
    }

    function name() public view override returns (string memory) {
        return _name;
    }

    function symbol() public view override returns (string memory) {
        return _symbol;
    }

    function decimals() public view override returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function mint(address account, uint256 amount) public onlyOwner {
        require(_maxSupplyEnabled == true, "ERC20: max supply reached");
        require(_totalSupply.add(amount) <= _maxSupply, "ERC20: max supply exceeded");
        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function burn(uint256 amount) public {
        _burn(msg.sender, amount);
    }
    function burnFrom(address account, uint256 amount) public {
        uint256 decreasedAllowance = _allowances[account][msg.sender].sub(amount, "ERC20: burn amount exceeds allowance");
        _approve(account, msg.sender, decreasedAllowance);
        _burn(account, amount);
    }

    function disableMaxSupply() public onlyOwner {
        _maxSupplyEnabled = false;
    }

    function enableMaxSupply() public onlyOwner {
        _maxSupplyEnabled = true;
    }

    function _transfer(address sender, address recipient, uint256 amount) internal {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(_balances[sender] >= amount, "ERC20: insufficient balance");

        _balances[sender] = _balances[sender].sub(amount);
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _burn(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: burn from the zero address");
        require(_balances[account] >= amount, "ERC20: insufficient balance for burning");

        _balances[account] = _balances[account].sub(amount);
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    }
}

contracts/LiquidityProvider.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";

contract UniswapLiquidityProvider {
    address private uniswapRouter;
    address private token;
    bool private liquidityLocked;
    uint256 private liquidityLockTime;
    address private authorizedAddress;

    constructor(address _token, address _uniswapRouter) {
        uniswapRouter = _uniswapRouter;
        token = _token;
        authorizedAddress = msg.sender;
    }

    modifier onlyAuthorized {
        require(msg.sender == authorizedAddress, "Unauthorized access");
        _;
    }

    function addLiquidity(uint256 amountTokenDesired, uint256 amountETHDesired) public onlyAuthorized {
        IUniswapV2Router02 router = IUniswapV2Router02(uniswapRouter);
        require(router != IUniswapV2Router02(address(0)), "Uniswap router not found");
        IERC20(token).approve(address(router), amountTokenDesired);
        router.addLiquidityETH{value: amountETHDesired}(token, amountTokenDesired, 0, 0, address(this), block.timestamp + 1800);
        lockLiquidity();
    }

    function lockLiquidity() public onlyAuthorized {
        require(!liquidityLocked, "Liquidity already locked");
        liquidityLocked = true;
        liquidityLockTime = block.timestamp + 1800; // 30 minutes lock time
    }

    function unlockLiquidity() public onlyAuthorized {
        require(liquidityLocked, "Liquidity not locked");
        require(block.timestamp > liquidityLockTime, "Liquidity still locked");
        liquidityLocked = false;
    }

    function setAuthorizedAddress(address _address) public onlyAuthorized {
        authorizedAddress = _address;
    }

    function withdrawTokens(address tokenAddress) public onlyAuthorized {
        IERC20 tokenInstance = IERC20(tokenAddress);
        uint256 balance = tokenInstance.balanceOf(address(this));
        tokenInstance.transfer(msg.sender, balance);
    }

    function withdrawETH() public onlyAuthorized {
        uint256 balance = address(this).balance;
        payable(msg.sender).transfer(balance);
    }

    receive() external payable {}
}

contracts/Lock.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

contract Lock {
    uint public unlockTime;
    address payable public owner;

    event Withdrawal(uint amount, uint when);

    constructor(uint _unlockTime) payable {
        require(
            block.timestamp < _unlockTime,
            "Unlock time should be in the future"
        );

        unlockTime = _unlockTime;
        owner = payable(msg.sender);
    }

    function withdraw() public {
        // Uncomment this line, and the import of "hardhat/console.sol", to print a log in your terminal
        // console.log("Unlock time is %o and block timestamp is %o", unlockTime, block.timestamp);

        require(block.timestamp >= unlockTime, "You can't withdraw yet");
        require(msg.sender == owner, "You aren't the owner");

        emit Withdrawal(address(this).balance, block.timestamp);

        owner.transfer(address(this).balance);
    }
}

